import React from 'react';
import ReactDom from 'react-dom';

// const element = React.createElement('h2', 'h2', 'Hello world!');

const AppHeader = () => {
  const margin = 40;
  const headerStyle = {
    color: 'red',
    marginLeft: `${margin}px`,
    marginBottom: `${margin}px`,
    fontSize: '40px',
    textTransform: 'uppercase'
  }
  return (
    <h1 style={headerStyle}> Science proves there are six ways you can learn and retain something faster.</h1>
  );
}

const AppList = () => {
  const items = ['teach', 'love', 'repeat', 'play', 'joy'];

  const firstItem = <li>First</li>;

  const isAuth = false;

  return (
    <ul>
        {isAuth ? firstItem : null}
        <li>{ items[0] }</li>
        <li>{ items[1] }</li>
        <li>{ items[2] }</li>
        {items.map(item => <li>item</li>)}
        <li>Teach Someone Else (Or Just Pretend To).</li>
        <li>Learn In Short Bursts of Time.</li>
        <li>Take Notes By Hand.</li>
        <li>Use The Power of Mental Spacing.</li>
        <li>Take A Study Nap.</li>
        <li>Change It Up.</li>
      </ul>
  );
}

const AppInput = () => {
const placeholder = 'Type text...';

  return (
    <label>
      <input placeholder={placeholder}/>
    </label>
  )
}

  const App = () => {
    return (
    <React.Fragment>
      <AppHeader />
      <AppInput />
      <AppList />
    </React.Fragment>
    )
  }

ReactDom.render(<App />, document.getElementById('root'));
