import React from 'react';
import HeaderBlock from './components/HeaderBlock';
import TextBlock from './components/TextBlock';
import FooterBlock from './components/FooterBlock';
import ImageBlock from './components/ImageBlock';
import CardList from './components/CardList';
import churchOlga from './components/img/Church_of_st_Olha.jpg';
import churchMap from './components/img/Church_of_st_Olha_map.jpg';
import churchPotoz from './components/img/Palaz_Pototskyi.jpg';
import churchPotozMap from './components/img/Palaz_Pototskyi_map.jpg';
import churchYura from './components/img/Palaz_Yura.jpg';
import churchYuraMap from './components/img/Palaz_Yura_map.jpg';


export const placesList = [
  {
      text:'Церква святих Ольги і Єлизавети (Костел Святої Ельжбети)',
      descr: 'Костел святої Ельжбети своєю надзвичайною красою нагадує європейські храми в стилі готики. Тут і довгі шпилі, і стрілчасті арки, і розетта над входом, і «мереживна» обробка. Але за часів СРСР святиню не особливо цінували, але ж у неї – цікава історія. Храм в стилі неоготики почали будувати після загибелі імператриці Єлизавети, яка була дружиною Франца Йосифа І.',
      photo: churchOlga,
      map: churchMap
  },
    {
      text:'Палац графів Потоцьких',
      descr: 'Велична будівля в стилі бароко прикрашає вулицю Коперника у Львові. Місця землі раніше називали Хорунщиною, адже володів ними хорунжий Стефан Потоцький ще у XVII ст. Граф Альфред ІІ Потоцький, будучи намісником Галичини, в кінці XIX ст. Задумав вибудувати палац на місці, де до цього знаходився парк і мисливська садиба.',
      photo: churchPotoz,
      map: churchPotozMap
  },
  {
    text:'Архикафедральний собор святого Юра',
    descr: 'На Юр\'євій горі, на висоті 300 м, розташована найкрасивіша святиня Львову – собор Святого Юра. Грекокатолицькому храму майже сімсот років і виконаний він у стилі бароко. Тут знаходиться чудотворна ікона Пресвятої Діви Марії, датована ХVІІ століттям. Раніше на місці храму стояла церква з дерева, а також монастир, зведені за часів Галицько-Волинського князівства.',
    photo: churchYura,
    map: churchYuraMap
  }
];


const App = () => {
  return (
      <>
        <HeaderBlock 
          title="МАНДРУЄМО УКРАЇНОЮ"
          descr="Обери тур до душі"
        >
        </HeaderBlock>
        
        <TextBlock />
        
        <ImageBlock />

        <div>
          <CardList item={placesList} />
        </div>
        <FooterBlock />
      </>
  );
}
export default App;
