import React from 'react';
import HeaderBlock from './components/HeaderBlock';
import TextBlock from './components/TextBlock';
import FooterBlock from './components/FooterBlock';
import ImageBlock from './components/ImageBlock';
import Card from './components/Card';
import CardList from './components/CardList';

export const placesList = [
  {
      text:'Церква святих Ольги і Єлизавети (Костел Святої Ельжбети)',
      descr: 'Костел святої Ельжбети своєю надзвичайною красою нагадує європейські храми в стилі готики. Тут і довгі шпилі, і стрілчасті арки, і розетта над входом, і «мереживна» обробка. Але за часів СРСР святиню не особливо цінували, але ж у неї – цікава історія. Храм в стилі неоготики почали будувати після загибелі імператриці Єлизавети, яка була дружиною Франца Йосифа І. З усіх проектів кращим став креслення Теодора Тальовський. Зведення почалося в 1903-м на кошти працівників залізниці, саме з цієї причини одну з каплиць назвали на честь святого Губерта, який був захисником залізничників. На особовому фасаді знаходиться фігура Ісуса Христа авторства Петра Війтавіча.',
      photo: '../img/Church_of_st_Olha.jpg',
      map: '../img/Church_of_st_Olha_map.jpg'
  },
  {
      text:'la-la-la',
      photo: 'between',
      map: 'между'
  },
  {
      text:'la-la-la',
      photo: 'between',
      map: 'между'
  }
];


const App = () => {
  return (
      <>
        <HeaderBlock 
          title="МАНДРУЄМО УКРАЇНОЮ"
          descr="Обери тур до душі"
        >
        </HeaderBlock>
        
        <TextBlock />
        
        <ImageBlock />

        <div>
          <CardList item={placesList} />
        </div>
        <FooterBlock />
      </>
  );
}
export default App;
