import React from 'react';
import s from './HeaderBlock.module.css';

import ReactLogo from '../HeaderBlock/img/logo.png';



const HeaderBlock = ({title, hideBackground = false, descr}) => {
    const styleCover = hideBackground ? {backgroundImage: 'none'} : {};

    return (
        <div>
            <div className={s.nav}>
                <img className={s.logo}/>
                <ul>
                    <li><a>Country guides</a></li>
                    <li><a>City guides</a></li>
                    <li><a>Features</a></li>
                    <li><a>Food and Drink</a></li>
                    <li><a>Flights</a></li>
                    <li><a>Shop</a></li>
                </ul>
            </div>
            <div className={s.cover} style={styleCover}>
                <div className={s.wrap}>
                    {title ? <h1 className={s.header}>{title}</h1> : null}
                    <img src={ReactLogo}/>
                    <p className={s.descr}>{descr}</p>
                </div>
            </div>
        </div>
    );
}

export default HeaderBlock;
